mapper.select:
select

from bb_dish

    del_flag = 0
    and create_user_id = #{createUserId}

        and season = #{season}


        and oily_degree = #{oilyDegree}


        and dish_type = #{dishType}


        and dish_name = #{dishName}


order by dish_id desc

mapper.update:
UPDATE bb_dish
season = #{season}, oily_degree = #{oilyDegree}, dish_type = #{dishType}, dish_name = #{dishName}, dish_pic = #{dishPic}, dish_source = #{dishSource}, dish_desc = #{dishDesc},
WHERE dish_id = #{dishId} AND create_user_id = #{createUserId}

mapper.delete:
UPDATE bb_dish
SET del_flag = 1
WHERE dish_id = #{dishId}
AND create_user_id = #{createUserId}
